var fs = require('fs');
var iconv = require('iconv-lite');
//var str = '';
exports.show = function(req,res){
//    console.log(req.headers);
    if (req.files && req.files.music != 'undifined') {
        var temp_path = req.files.music.path;
        if (temp_path) {
            var obj = req.files.music;
            var tmp_path = obj.path;
            var new_path = "staticfile/music/"+obj.name;
//            console.log("原路径："+tmp_path);
//            console.log("新路径："+new_path);
            fs.rename(tmp_path,new_path,function(err,re){
                if(err){
                    console.log(err);
                }
                else{
                    console.log(new_path);
                }
            });
            var show_path= "music/"+obj.name;
            var a={path:show_path};
            res.json(a);
        }
    }
};
exports.canvas = function(req,res){
    var bufferStr = req.body.dataStr.replace(/\s/i,"+");
    var dataBuffer = new Buffer(bufferStr, 'base64');
    var fileRoute = "images/touxiang"+(new Date()).getTime()+".png";
    fs.writeFile("staticfile/"+fileRoute, dataBuffer, function(err) {
        if(err){
            res.write(err);
        }else{
            console.log("OK : "+fileRoute);
            res.write(fileRoute);
        }
        res.end();
    });
};
